const MessageBubble = ({ message, isLatest }) => {
    const messageRef = React.useRef(null);

    React.useEffect(() => {
        if (isLatest && messageRef.current) {
            messageRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
    }, [isLatest]);

    if (!message) return null;

    return (
        <div
            ref={messageRef}
            className={`flex gap-2 ${
                message.type === 'user' ? 'justify-end' : 'justify-start'
            }`}
        >
            <div
                className={`rounded-lg p-4 max-w-[80%] ${
                    message.type === 'user'
                        ? 'bg-crystal-500'
                        : message.type === 'card'
                        ? 'bg-mystic-700'
                        : 'bg-mystic-800'
                }`}
            >
                {message.card && (
                    <div className="mb-4">
                        <h3 className="text-xl font-semibold mb-2 text-crystal-500 font-cinzel">
                            {message.card.name}
                        </h3>
                        <img
                            src={message.card.image}
                            alt={message.card.name}
                            className="rounded-lg mb-2 w-full max-w-[240px] mx-auto shadow-lg"
                        />
                    </div>
                )}
                {message.type === 'user' ? (
                    <p className="text-sm text-white whitespace-pre-line">
                        {message.content || ''}
                    </p>
                ) : (
                    <TypedMessage 
                        content={message.content || ''} 
                    />
                )}
            </div>
        </div>
    );
};

window.MessageBubble = MessageBubble;
