import React, { useState } from 'react';
import Header from './components/Header';
import InputSection from './components/InputSection';
import DrawButton from './components/DrawButton';
import MessageBubble from './components/MessageBubble';

function App() {
    const [messages, setMessages] = useState([]);

        const handleDrawCard = () => {
            const cards = [
                { name: "The Fool", meaning: "New beginnings, optimism, trust in life" },
                { name: "The Magician", meaning: "Action, the power to manifest" },
                { name: "The High Priestess", meaning: "Inaction, going within, the mystical" },
            ];
            const drawnCard = cards[Math.floor(Math.random() * cards.length)];

            const newMessage = {
                id: Date.now(),
                content: `You drew ${drawnCard.name}: ${drawnCard.meaning}`,
                type: "ai",
            };
            setMessages([...messages, newMessage]);
        };

    return (
        <div className="bg-mystic-900 text-crystal-500 min-h-screen flex flex-col">
            <Header />
            <div className="flex-1 p-4">
                <div className="space-y-4">
                    {messages.map((message) => (
                        <MessageBubble key={message.id} message={message} />
                    ))}
                </div>
                <InputSection />
                <DrawButton onClick={handleDrawCard} />
            </div>
        </div>
    );
}

export default App;
